declare module '*.svg' {
  const content: string;
  export default content;
}

declare module "*.png" {
  const content: string;
  export default content;
}

declare module '*.jpg' {
  const content: string;
  export default content;
}

declare module "*.png" {
  const src: string
  export default src
}

declare module "*.module.css" {
  const src: any
  export default src
}


declare module '*.png';
declare module '*.jpg';